        }
      }
      if (event.title_3 !== "" && event.link_3 !== "") {
        links['link_3'] = {

          title: event.title_3,
          url: event.link_3,
        }
      }

      const data = {
        listen_links: event.listen_now_link,
        update_type: 'links',
        links,
      }
      await props.updateUserProfile(data);
    }
  };

